
public class Increment_assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
int x=10;
//incrementing value
//x=x+1;
//post-increment
//x++;
System.out.println(x++);
System.out.println(x);
//pre-increment
System.out.println(++x);
System.out.println(x);
//Assignment operator usage
x+=5;//x=x+5(short form)
x*=5;
x-=5;
	}

}
