
public class String {

	public static void main(java.lang.String[] args) {
		// TODO Auto-generated method stub
//string is a sequence of character or character array
		java.lang.String mystring="You can do it";
		java.lang.String mygoal="You are a java programmer";
		System.out.println(mystring);
		System.out.println(mygoal);
		System.out.println(mystring.length());
		System.out.println(mygoal.toUpperCase());
		java.lang.String new1="Hello" + "world";
		System.out.println(new1);
		System.out.println(new1.replace('e', 'a'));
		System.out.println(new1.indexOf('o'));
	}

}
